mapper.select:
    
    SELECT * FROM product
    LEFT JOIN product_image ON
    product.product_id = product_image.product_id
    
		
			
				and sold_check = #{soldCheck}
			
			
				and user_id = #{userId}
			
			
				and name = #{name}
			
			
				and product.product_name like CONCAT('%', #{search}, '%')
			
		
		
			
				
                ORDER BY product.product_date ASC
				
				
                ORDER BY product.product_date DESC
				
				
			
		
		LIMIT #{row}

mapper.update:
UPDATE product
SET sold_check = #{soldCheck}
WHERE
product_id=#{productId}
